import React, { useState, Fragment, useEffect } from 'react'
import { BrowserRouter as Router, Switch, Route, Link } from "react-router-dom";

//components 

import AddCountryForm from './forms/AddCountryForm'
import EditCountryForm from './forms/EditCountryForm'
import CountryForm from './forms/CountryForm'
import axios from 'axios';
import './App.css'

const App = () => {

	// Data

		const [countriesData,setCountriesData] = useState([]);



	const initialFormState = { id: null, name: '', capital: '' }

	// Setting state

	const [ countries, setCountries ] = useState(countriesData)
	const [ curCountry, setCurCountry ] = useState(initialFormState)
	const [ edit, setEdit ] = useState(false)

	// CRUD operations

	const addCountry = async (country) => {
		const id = await axios.post(`http://localhost:4000/`,country);
		getData();
	}

	const deleteCountry = async (id) => {
		setEdit(false)
		await axios.delete(`http://localhost:4000/${id}`);
		getData();
	}

	const updateCountry = async (id, updatedCountry) => {
		setEdit(false)
		console.log(id);
		await axios.put(`http://localhost:4000/${id}`,updatedCountry);
		getData();
	}
	const getData = async () =>{
			const {data} = await axios.get('http://localhost:4000');
			setCountriesData(data);
			setCountries(data)
	}
	useEffect(()=>{
		getData();
	},[])

	const editRow = async (country) => {
		setEdit(true)
		setCurCountry({ id: country.id, name: country.name, capital: country.capital })
	}

	return (
	<Router>
		<div>
			<nav>
				<ul>
					<li>
						<Link to="/" >Home</Link>
				  	</li>
					<li>
						<Link to="/about">About</Link>
				  	</li>
				  	<li>
						<Link to="/countries">Countries</Link>
				  	</li>
				</ul>
			</nav>

			<Switch>
				<Route path="/about">
					<About />
				</Route>
				<Route path="/countries">
					<div className="container">
						<h1 id = "hed1">Application</h1>
						<div className="flex-row">
							<div className="flex-large classleft">
								{edit ? (
								<Fragment>
									<h2>Edit</h2>
									<EditCountryForm
									edit={edit}
									setEdit={setEdit}
									curCountry={curCountry}
									updateCountry={updateCountry}
									/>
								</Fragment>
								) : (
								<Fragment>
									<h2>Fill the brackets</h2>
									<AddCountryForm addCountry={addCountry} />
								</Fragment>
								)}
							</div>
							<div className="flex-large classright">
								<h2>View</h2>
								<CountryForm countries={countries} editRow={editRow} deleteCountry={deleteCountry} />
							</div>
						</div>
					</div>
				</Route>
				<Route path="/">
					<Home />
				</Route>
			</Switch>
		</div>
	</Router>
	)

	function Home() {
		return <h1 id = "hed2">Home</h1>;
	  }

	  function About() {
		return <h1 id = "hed3">About</h1>;
	  }
}

export default App
