import React, { useState, Fragment } from 'react';

	const AddCountryForm = props => {




	const [ country, setCountry ] = useState("")

	const onSubmitForm =  async e => {

        e.preventDefault();
        try{
            const body= {country};
            const response = await fetch("http://localhost:3000/countries", {
                method : "POST",
                headers : {"Content-Type": "application/json"},
                body: JSON.stringify(body)
        });

        window.location = "/"; // refresh show the changes
        }catch(err) {
            console.error(err.message)
        }

    }

	return (

		<Fragment>
		<form className="d-flex mt-5"
			onSubmit={e => {
				e.preventDefault()
				if (!country.name || !country.capital) return

				props.addCountry(country)	
				setCountry();
			}}
		>
			<label className="text-center mt-5">Input Countries</label>
			<input
			type="text" 
			className="form-control"
			name="name"
			value={country.name} 
			onChange={e => setCountry(e.target.value)} />
			<p>
				<label className="text-center mt-5">Capital</label>
				<input type="text" name="capital" value={country.capital} onChange={onSubmitForm} />
			</p>
			<p>
				<button className="btn btn-success">Add new country</button>
			</p>
		</form>
		</Fragment>
	);
};

export default AddCountryForm
